!function($, window, document) {
    console.log("init");

    var o = {
        init: function() {
            o.select2();
            o.stellar();
            o.carousel();
            o.portfolio();
            o.lightCase();
            o.sideNav();
            o.counter();
            o.skills();
            o.aos();
            o.navbarChange();
        },
        select2: function() {
            $(document).ready(function() {
                $("select").select2();
            });
        },
       
        carousel: function() {
            $(".testi-carousel").owlCarousel({
                loop: true,
                margin: 10,
                autoplay: true,
                nav: false,
                dots: true,
                dotSpeed: 1000,
                autoplaySpeed: 1000,
                items: 1
            });

            $(".client-slider").owlCarousel({
                loop: true,
                margin: 10,
                nav: true,
                navSpeed: 1000,
                dots: false,
                autoplay: true,
                autoplaySpeed: 1000,
                navText: ['<span class="fa fa-angle-left"></span>', '<span class="fa fa-angle-right"></span>'],
                responsive: {
                    0: {
                        items: 1
                    },
                    600: {
                        items: 3
                    },
                    1000: {
                        items: 6
                    }
                }
            });
        },
    
        lightCase: function() {
            $(document).ready(function() {
                $("a[data-rel^=lightcase]").lightcase();
            });
        },
        sideNav: function() {
            $("#side-nav-open").click(function() {
                $("#side-nav").css("width", "300");
                setTimeout(function() {
                    $("body").addClass("sidenav-open");
                }, 200);
                setTimeout(function() {
                    $("body").addClass("in");
                }, 400);
            });

            $("#side-nav-close, #canvas-overlay").click(function() {
                setTimeout(function() {
                    $("body").removeClass("in");
                }, 200);
                setTimeout(function() {
                    $("body").removeClass("sidenav-open");
                    $("#side-nav").css("width", "0");
                }, 400);
            });

            $("#side-search-open").click(function() {
                $("#side-search").css("width", "300");
                setTimeout(function() {
                    $("body").addClass("sidesearch-open");
                }, 200);
                setTimeout(function() {
                    $("body").addClass("in");
                }, 300);
            });

            $("#side-search-close, #canvas-overlay").click(function() {
                setTimeout(function() {
                    $("body").removeClass("in");
                }, 200);
                setTimeout(function() {
                    $("body").removeClass("sidesearch-open");
                    $("#side-search").css("width", "0");
                }, 300);
            });
        },
     
    
        aos: function() {
            AOS.init({ once: true });
        },
        navbarChange: function() {
            $(window).scroll(function() {
                var scrollTop = $(window).scrollTop();
                if (scrollTop > 150) {
                    $("#header-navbar").removeClass("navbar-transparent");
                    $("body").addClass("not-on-top");
                } else {
                    $("body").removeClass("not-on-top");
                    $("#header-navbar").addClass("navbar-transparent");
                }
            });
        }
    };

    $(document).ready(function() {
        o.init();
    });

}(jQuery, window, document);
